describe("Creating custom map controls",function(){var t;beforeEach(function(){t=t||new GMaps({el:"#basic-map",lat:-12.0433,lng:-77.0283,zoom:12})}),it("should add default styles for the control",function(){t.addControl({position:"top_right",content:"Geolocate"}),expect(t.controls[0].position).toEqual(google.maps.ControlPosition.TOP_RIGHT),expect(t.controls[0].style.fontFamily).not.toEqual("")}),it("should leave off default styles if requested",function(){t.addControl({position:"top_right",disableDefaultStyles:!0,content:'<i class="icon"></i>'}),expect(t.controls[1].position).toEqual(google.maps.ControlPosition.TOP_RIGHT),expect(t.map.controls[google.maps.ControlPosition.TOP_RIGHT].length).toEqual(2),expect(t.controls[1].style.fontFamily).toEqual("")}),it("should remove control",function(){var o=t.controls[0];t.removeControl(o),expect(t.controls.length).toEqual(1),expect(t.map.controls[google.maps.ControlPosition.TOP_RIGHT].length).toEqual(1),expect(t.controls[0]).not.toEqual(o)})});