describe("Creating a marker",function(){var t,r;beforeEach(function(){t=t||new GMaps({el:"#map-with-markers",lat:-12.0533,lng:-77.0293,zoom:14})}),describe("With basic options",function(){beforeEach(function(){r=t.addMarker({lat:-12.0533,lng:-77.0293,title:"New marker"})}),it("should add the marker to the markers collection",function(){expect(t.markers.length).toEqual(1),expect(t.markers[0]).toEqual(r)}),it("should create a marker with defined position",function(){expect(parseFloat(r.getPosition().lat().toFixed(4))).toEqual(-12.0533),expect(parseFloat(r.getPosition().lng().toFixed(4))).toEqual(-77.0293)})}),describe("With events",function(){var e;beforeEach(function(){e={onclick:function(){console.log(this.title)}},spyOn(e,"onclick").andCallThrough(),r=t.addMarker({lat:-12.0533,lng:-77.0193,title:"New marker",click:e.onclick})}),it("should respond to click event",function(){google.maps.event.trigger(r,"click"),expect(e.onclick).toHaveBeenCalled()})})}),describe("Removing markers",function(){var t;beforeEach(function(){(t=t||new GMaps({el:"#map-with-markers",lat:-12.0533,lng:-77.0293,zoom:14})).removeMarkers(),t.addMarkers([{lat:-12.0523,lng:-77.0297,title:"Marker #1"},{lat:-12.0531,lng:-77.0289,title:"Marker #2"},{lat:-12.0537,lng:-77.0299,title:"Marker #3"},{lat:-12.0532,lng:-77.0278,title:"Marker #4"}])}),it("should remove a marker from the markers collection",function(){t.removeMarker(t.markers[0]),expect(t.markers.length).toEqual(3)}),it("should remove an array of markers from the markers collection",function(){var e=[t.markers[0],t.markers[2]];t.removeMarkers(e),expect(t.markers.length).toEqual(2)})});